package org.ireslab.notification.bean;

// TODO: Auto-generated Javadoc
/**
 * The Class SmsUser.
 */
public class SmsUser {

	/** The user mobile number. */
	private String userMobileNumber;

	/** The text message. */
	private String textMessage;

	/**
	 * Gets the user mobile number.
	 *
	 * @return the user mobile number
	 */
	public String getUserMobileNumber() {
		return userMobileNumber;
	}

	/**
	 * Sets the user mobile number.
	 *
	 * @param userMobileNumber
	 *            the new user mobile number
	 */
	public void setUserMobileNumber(String userMobileNumber) {
		this.userMobileNumber = userMobileNumber;
	}

	/**
	 * Gets the text message.
	 *
	 * @return the text message
	 */
	public String getTextMessage() {
		return textMessage;
	}

	/**
	 * Sets the text message.
	 *
	 * @param textMessage
	 *            the new text message
	 */
	public void setTextMessage(String textMessage) {
		this.textMessage = textMessage;
	}

}
